    private void passKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_passKeyPressed
      
        if(evt.getKeyCode()==KeyEvent.VK_ENTER){
            IniciarSesionActionPerformed(null);
        }
    }//GEN-LAST:event_passKeyPressed

    private void IniciarSesionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_IniciarSesionActionPerformed
String usere=user.getText();
String password= new String(pass.getText());
if(usere.equals(usuario) && password.equals(contraseña)){
  JOptionPane.showMessageDialog(null, "Bienvenido a tu menu de apss");
   Menu_Principal ventana = new Menu_Principal();
    this.setVisible(false);
    ventana.setVisible(true);
    ventana.setLocationRelativeTo(null);
  }else if (usere.equals(usuario) && password.isEmpty()){
    JOptionPane.showMessageDialog(null, "No has ingresado  la contraseña");    
  }else if(usere.isEmpty() && password.equals(contraseña)){
   JOptionPane.showMessageDialog(null, "No has ingresado el usuario");
  }else if(usere.compareTo(usuario)>0 && password.compareTo(contraseña)>0){
  JOptionPane.showMessageDialog(null, "Usuario erroneo y contraseña erronea"); 
  }else if(usere.equals(usuario) && password.compareTo(contraseña)>0){
  JOptionPane.showMessageDialog(null, "Usuario ingresado correctamente y contraseña erronea"); 
  }else if(usere.compareTo(usuario)>0 && password.equals(contraseña)){
  JOptionPane.showMessageDialog(null, "Usuario erroeneo y contraseña ingresada correctamente"); 
  }else if(usere.isEmpty() && password.isEmpty()){
   JOptionPane.showMessageDialog(null, "No has ingresado el usuario y la contraseña");
  }else if(usere.compareTo(usuario)<0 && password.compareTo(contraseña)<0){
  JOptionPane.showMessageDialog(null, "Usuario erroneo y contraseña erronea"); 
  }else if(usere.equals(usuario) && password.compareTo(contraseña)<0){
  JOptionPane.showMessageDialog(null, "Usuario ingresado correctamente y contraseña erronea"); 
  }else if(usere.compareTo(usuario)<0 && password.equals(contraseña)){
  JOptionPane.showMessageDialog(null, "Usuario erroeneo y contraseña ingresada correctamente");
  }   
      
    }//GEN-LAST:event_IniciarSesionActionPerformed

