    private void userKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_userKeyPressed
        if(evt.getKeyCode()==KeyEvent.VK_ENTER){
          pass.requestFocus();
        }
    }//GEN-LAST:event_userKeyPressed

